LwlStatus
lwswitch_thermal_tsense_clear_ls10
(
    lwswitch_device *device
)
{
    LwU32 command;
    LwBool keepPolling;
    LWSWITCH_TIMEOUT timeout;

    // .Thermal Tsense Clear - WAR 
    
    // _Note_ : Added on 8/11/2021 
    
    
    // _Note_ : Clear TSENSE after clock switch from 100MHz to 1MHz detail of workaround dolwmented in link:++https://lwbugs/3330568++[Bug 3330568] 
    
    
    // . Toggle TS_PD to clear TSENSE internal state
    
    // .. Set CONTROL_SRC to REG, assert TS_PD 
    
    LWSWITCH_REG_WR32(device, _THERM, _GPC_TSENSE,
                           DRF_DEF(_THERM, _GPC_TSENSE, _CONTROL_SRC, _REG) |
                           DRF_DEF(_THERM, _GPC_TSENSE, _TS_PD, _ASSERT));
    
    LWSWITCH_REG_WR32(device, _THERM, _GPC_TSENSE_CONFIG_COPY,
                           DRF_DEF(_THERM, _GPC_TSENSE_CONFIG_COPY, _NEW_SETTING, _SEND));
    
    // .. Wait for send to complete 
    
    lwswitch_timeout_create(LWSWITCH_INTERVAL_1MSEC_IN_NS, &timeout);
    
    do 
    {
        keepPolling = (lwswitch_timeout_check(&timeout)) ? LW_FALSE : LW_TRUE;
        command = LWSWITCH_REG_RD32(device, _THERM, _GPC_TSENSE_CONFIG_COPY);
    
        if ( FLD_TEST_DRF(_THERM, _GPC_TSENSE_CONFIG_COPY, _NEW_SETTING, _NO_CMD, command) )
        {
            break;
        }
        if ( keepPolling == LW_FALSE )
        {
            LWSWITCH_PRINT(device, ERROR, "%s: -- Poll timeout for THERM 0 _THERM _GPC_TSENSE_CONFIG_COPY --\n", __FUNCTION__);
            return -LWL_ERR_GENERIC;
        }
    }
    while (keepPolling);
    
    // . Hold TS_PD=1 for at least 3us to power down circuit since TSENSE_CONFIG_COPY goes thru serializer (>100 bits) at 1MHz no explicit wait needed, TS_PD guaranteed to be 1 for at least 100us 
    
    // . Deassert TS_PD while we have CONTROL_SRC in REG 
    
    LWSWITCH_REG_WR32(device, _THERM, _GPC_TSENSE,
                           DRF_DEF(_THERM, _GPC_TSENSE, _CONTROL_SRC, _REG) |
                           DRF_DEF(_THERM, _GPC_TSENSE, _TS_PD, _DEASSERT));
    
    LWSWITCH_REG_WR32(device, _THERM, _GPC_TSENSE_CONFIG_COPY,
                           DRF_DEF(_THERM, _GPC_TSENSE_CONFIG_COPY, _NEW_SETTING, _SEND));
    
    // .. Wait for send to complete 
    
    lwswitch_timeout_create(LWSWITCH_INTERVAL_1MSEC_IN_NS, &timeout);
    
    do 
    {
        keepPolling = (lwswitch_timeout_check(&timeout)) ? LW_FALSE : LW_TRUE;
        command = LWSWITCH_REG_RD32(device, _THERM, _GPC_TSENSE_CONFIG_COPY);
    
        if ( FLD_TEST_DRF(_THERM, _GPC_TSENSE_CONFIG_COPY, _NEW_SETTING, _NO_CMD, command) )
        {
            break;
        }
        if ( keepPolling == LW_FALSE )
        {
            LWSWITCH_PRINT(device, ERROR, "%s: -- Poll timeout for THERM 0 _THERM _GPC_TSENSE_CONFIG_COPY --\n", __FUNCTION__);
            return -LWL_ERR_GENERIC;
        }
    }
    while (keepPolling);
    
    // . Set CONTROL_SRC back to LOGIC/HW 
    
    LWSWITCH_REG_WR32(device, _THERM, _GPC_TSENSE,
                           DRF_DEF(_THERM, _GPC_TSENSE, _CONTROL_SRC, _LOGIC));
    
    LWSWITCH_REG_WR32(device, _THERM, _GPC_TSENSE_CONFIG_COPY,
                           DRF_DEF(_THERM, _GPC_TSENSE_CONFIG_COPY, _NEW_SETTING, _SEND));
    
    // .. Wait for send to complete 
    
    lwswitch_timeout_create(LWSWITCH_INTERVAL_1MSEC_IN_NS, &timeout);
    
    do 
    {
        keepPolling = (lwswitch_timeout_check(&timeout)) ? LW_FALSE : LW_TRUE;
        command = LWSWITCH_REG_RD32(device, _THERM, _GPC_TSENSE_CONFIG_COPY);
    
        if ( FLD_TEST_DRF(_THERM, _GPC_TSENSE_CONFIG_COPY, _NEW_SETTING, _NO_CMD, command) )
        {
            break;
        }
        if ( keepPolling == LW_FALSE )
        {
            LWSWITCH_PRINT(device, ERROR, "%s: -- Poll timeout for THERM 0 _THERM _GPC_TSENSE_CONFIG_COPY --\n", __FUNCTION__);
            return -LWL_ERR_GENERIC;
        }
    }
    while (keepPolling);
    
    // . SW must wait ~2100 TS_CLK (2.1ms at 1MHz) after 1->0 transition before using TSENSE output LWPEX2 wait only allow sleep in unit of second, sleep for 1 sec 

    return LWL_SUCCESS;
}


