LwlStatus
lwswitch_thermal_init_ls10
(
    lwswitch_device *device
)
{
    LwU32 command;
    LwBool keepPolling;
    LWSWITCH_TIMEOUT timeout;

    // .Thermal Pin Open Drain 
    
    // _Note_ : Added on 8/11/2021 
    
    
    // _Note_ : Enable open drain/outbound mode for GPIO pins, dolwmented in link:++https://lwbugs/3294730++[Bug 3294730] 
    
    
    // . Setup GPIO pin 9/12 for open drain, IO_OUT_EN=NO 
    
    LWSWITCH_REG_WR32(device, _GPIO, _OUTPUT_CNTL(9),
                           DRF_NUM(_GPIO, _OUTPUT_CNTL, _IO_INPUT, 0x0) |
                           DRF_DEF(_GPIO, _OUTPUT_CNTL, _OPEN_DRAIN, _ENABLE) |
                           DRF_DEF(_GPIO, _OUTPUT_CNTL, _SEL, _THERMAL_SLOWDOWN));
    
    LWSWITCH_REG_WR32(device, _GPIO, _OUTPUT_CNTL(12),
                           DRF_NUM(_GPIO, _OUTPUT_CNTL, _IO_INPUT, 0x0) |
                           DRF_DEF(_GPIO, _OUTPUT_CNTL, _OPEN_DRAIN, _ENABLE) |
                           DRF_DEF(_GPIO, _OUTPUT_CNTL, _SEL, _VID_PWM));
    
    // . Apply GPIO setting 
    
    LWSWITCH_REG_WR32(device, _PMGR, _GPIO_OUTPUT_CNTL_TRIGGER,
                           DRF_DEF(_PMGR, _GPIO_OUTPUT_CNTL_TRIGGER, _UPDATE, _TRIGGER));
    
    lwswitch_timeout_create(LWSWITCH_INTERVAL_1MSEC_IN_NS, &timeout);
    
    do 
    {
        keepPolling = (lwswitch_timeout_check(&timeout)) ? LW_FALSE : LW_TRUE;
        command = LWSWITCH_REG_RD32(device, _PMGR, _GPIO_OUTPUT_CNTL_TRIGGER);
    
        if ( FLD_TEST_DRF(_PMGR, _GPIO_OUTPUT_CNTL_TRIGGER, _UPDATE, _DONE, command) )
        {
            break;
        }
        if ( keepPolling == LW_FALSE )
        {
            LWSWITCH_PRINT(device, ERROR, "%s: -- Poll timeout for PMGR 0 _PMGR _GPIO_OUTPUT_CNTL_TRIGGER --\n", __FUNCTION__);
            return -LWL_ERR_GENERIC;
        }
    }
    while (keepPolling);

    return LWL_SUCCESS;
}


